import React from "react";
import Button from "../Button/Button";
import "./Modal.css";

const Modal = (props) => {
  const { open, close, header } = props;

  return (
    <div>
      {open ? null : (
        <section>
          <header>{header}</header>
          <main>{props.children}</main>
          <footer>
            <Button onClick={props.onclose}>Confirm</Button>
          </footer>
        </section>
      )}
    </div>
  );
};
export default Modal;
