import React, { useState } from "react";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import styles from "./CSS/UserInput.module.css";

const UserInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [enteredValue, setEnteredValue] = useState("");
  const [isValid, setIsValid] = useState(true);

  const nameInuptChangeHandler = event => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setEnteredName(event.target.value);
  };

  const ageInuptChangeHandler = event => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setEnteredAge(event.target.value);
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    setEnteredValue(prevEnteredValue => {
      const updatedEnteredValue = {...prevEnteredValue}
      updatedEnteredValue = {
        name: enteredName,
        age: enteredAge
      };
      return updatedEnteredValue;
    })
    if (Object.keys(enteredValue).length === 0) {
      setIsValid(false);
      return;
    }
    props.onAddUser(enteredValue);
    setEnteredName("");
    setEnteredAge("");
  };

  return (
    <Card>
      <form onSubmit={formSubmitHandler}>
        <div className={styles['form-control']}>
          <label>Username</label>
          <input type="text" value={enteredName} onChange={nameInuptChangeHandler} />
          <label>Age (Years)</label>
          <input type="number" value={enteredAge} onChange={ageInuptChangeHandler} />
        </div>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};
export default UserInput;
