import Input from "../../UI/Input";
import classes from "./CSS/MealItemForm.module.css";

const MealItemForm = (props) => {
  const cas = () => {console.log('clicked');};
  return (
    <form className={classes.form}>
      <Input
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
          onChange: cas()
        }}
      />
      <button>+ Add</button>
    </form>
  );
};

export default MealItemForm;
