import React, { useState } from "react";
import "./CSS/ExpenseForm.css";

const ExpenseForm = (props) => {
  // 1. One State
  // const [ userInput, setUserInput ] = useState({
  //   enteredTitle: '', enteredAmount: '', enteredDate: ''
  // });

  // const titleChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredTitle: event.tartget.value };
  //   });
  // };
  // const amountChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredTitle: event.tartget.value };
  //   });
  // };
  // const dateChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, enteredTitle: event.tartget.value };
  //   });
  // };
  /* 2. Multy State */
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => { setEnteredTitle(event.target.value); };
  const amountChangeHandler = (event) => { setEnteredAmount(event.target.value); };
  const dateChangeHandler = (event) => { setEnteredDate(event.target.value); };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle('');
    setEnteredAmount('');
    setEnteredDate('');
  };

  const cancelHandler = (event) => {
    event.preventDefault();
    setFormContent(0);
  }

  const [formContent, setFormContent] = useState(0);
  const addNewExpenseHandler = (event) => {
    event.preventDefault();
    console.log('addNewExpenseHandler!');
    setFormContent(1);
  };

  return (
    <div>
      {formContent === 1 ?
        <form onSubmit={submitHandler} onCancel = {cancelHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              value={enteredTitle}
              onChange={titleChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              type="number"
              min="0.01"
              step="0.01"
              value={enteredAmount}
              onChange={amountChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              min="2019-01-01"
              max="2022-12-31"
              value={enteredDate}
              onChange={dateChangeHandler}
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button type="cancel">Cancel</button>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    : 
      <form onSubmit={addNewExpenseHandler}>
        <button type="submit">Add New Expense</button>
      </form>
    }
  </div>
  );
};
export default ExpenseForm;
