import React from "react";
import AuthContext from "../../../store/10_Reducer/auth-context";
import classes from "./Navigation.module.css";

const Navigation = (props) => {
  return (
    <AuthContext.Consumer>
      {(ctx) => {
        return (
          <nav className={classes.nav}><ul>
            {ctx.isLoggedIn && (<li><a href="/">Users</a></li>)}
            {ctx.isLoggedIn && (<li><a href="/">Admin</a></li>)}
            {ctx.isLoggedIn && (
              <li><button onClick={props.onLogout}>Logout</button></li>
            )}
          </ul></nav>
        );
      }}
    </AuthContext.Consumer>
  );
};
export default Navigation;
