import React, { useState } from "react";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import styles from "./CSS/UserInput.module.css";

const UserInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isValid, setIsValid] = useState(true);

  const nameInuptChangeHandler = event => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setEnteredValue(prevEnteredValue => {
      const updatedEnteredValue = {...prevEnteredValue, name: event.target.value};
      return updatedEnteredValue;
    })
  };

  const ageInuptChangeHandler = event => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setEnteredValue(prevEnteredValue => {
      const updatedEnteredValue = {...prevEnteredValue, age: event.target.value};
      return updatedEnteredValue;
    })
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    if (Object.keys(enteredValue) === 0) {
      setIsValid(false);
      console.log("빈 객체!");
      return;
    }
    props.onAddUser(enteredValue);
  };

  return (
    <Card>
      <form onSubmit={formSubmitHandler}>
        <div className={styles['form-control']}>
          <label>Username</label>
          <input type="text" onChange={nameInuptChangeHandler} />
          <label>Age (Years)</label>
          <input type="number" onChange={ageInuptChangeHandler} />
        </div>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};
export default UserInput;
